mapper.select:

    SELECT

    FROM t_user 
	WHERE 1=1 

       AND id = #{id,jdbcType=INTEGER}


         AND user_no = #{userNo,jdbcType=CHAR}


         AND auto_check_no = #{autoCheckNo,jdbcType=VARCHAR}


         AND ec_code = #{ecCode,jdbcType=VARCHAR}


         AND mobile = #{mobile,jdbcType=CHAR}


         AND password = #{password,jdbcType=CHAR}


         AND create_time = #{createTime,jdbcType=TIMESTAMP}


         AND status = #{status,jdbcType=INTEGER}


         AND lock_reason = #{lockReason,jdbcType=VARCHAR}


         AND ab_level = #{abLevel,jdbcType=VARCHAR}


         AND head_ico = #{headIco,jdbcType=VARCHAR}


         AND nickname = #{nickname,jdbcType=VARCHAR}


         AND sex = #{sex,jdbcType=INTEGER}


         AND love_state = #{loveState,jdbcType=INTEGER}


         AND favorite_snacks = #{favoriteSnacks,jdbcType=VARCHAR}


         AND plusv_certification_info = #{plusvCertificationInfo,jdbcType=VARCHAR}


         AND email = #{email,jdbcType=VARCHAR}


         AND area_id = #{areaId,jdbcType=INTEGER}


         AND recommend_user_id = #{recommendUserId,jdbcType=INTEGER}


         AND recommend_balance = #{recommendBalance,jdbcType=DECIMAL}


         AND account_id = #{accountId,jdbcType=INTEGER}


         AND upgrade_flag = #{upgradeFlag,jdbcType=INTEGER}


         AND last_login_time = #{lastLoginTime,jdbcType=TIMESTAMP}
